import React from 'react'
 import './ShoppingItem.css'
function ShoppingItem({cartItems}) {

  return (
    <div className="shopping__cart">
      <div className="numb__type">
        {cartItems.length === 0 ? (
          <div>
           <span>0</span>
          </div>
        ) : (
          <div>{cartItems.length} in this cart</div>
        )}
      </div>
      <div className="details__shopping">
        {cartItems.map((item) => (
          <div className="detail__shopping__item" key={item.id}>
            <div className="shopping__image">
              <img src={item.image} alt={item.title} width="100px" />
              <button>Remove</button>
            </div>
            <div className="shopping__count">
              <p>{item.title}</p>
              <p>
                {item.count}x{item.price}
              </p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default ShoppingItem
