import React, { useState } from 'react'
import './App.css';
import CartItem from './component/CartItem.js';
import Filter from './component/Filter';
import data from './data.json';


function App() {
  const [products, setproducts] = useState(data.products);
  const [Size, setSize] = useState('xl')
  const [Sort, setSort] = useState('HIGH')
  return (
    <div className="App">
      <header>
        <a href="/">React ShoppingCart</a>
      </header>
      <main>
        <div className="cart">
          <Filter
            product={products}
            Size={Size}
            Sort={Sort}
            setSize={setSize}
            setSort={setSort}
          />

          <CartItem product={products} setproducts Size={Size} Sort={Sort} />
        </div>
        <div className="shopping"> shoppingItem</div>
      </main>
      <footer>All rigths reserved.</footer>
    </div>
  );
}

export default App;
