import React, { useState } from 'react'
import data from '../data'
import './CartItem.css'
function CartItem({ product, Sort, Size, setproduct }) {
  const [products, setproducts] = useState(product);

  return (
    <div className="cartitem">
      {product
        .sort((a, b) =>
          Sort === "HIGH"
            ? a.price < b.price
              ? 1: -1
            : a.price > b.price
              ? 1 : -1
        )
        .filter((item) => item.availableSizes.indexOf(Size) > 0)
        .map((product) => (
          <div key={product.id}>
            <img
              src={product.image}
              alt={product.title}
              width="300px"
              height="350px"
            />
            { set}
            <div className="sub__title">
              <p>{product.price}</p>
              <p>{product.title}</p>
              <p>{ product.availableSizes.indexOf(Size)}</p>
            </div>
            <button className="additem__btn">add Item</button>
          </div>
        ))}
    </div>
  );
}

export default CartItem
