import React, { useState } from 'react'
import './App.css';
import CartItem from './component/CartItem.js';
import Filter from './component/Filter';
import ShoppingItem from './component/ShoppingItem';
import data from './data.json';


function App() {
  const [products, setproducts] = useState(data.products);
  const [Size, setSize] = useState('xxl')
  const [Sort, setSort] = useState('HIGH')
  const [, set] = useState(initialState)
  return (
    <div className="App">
      <header>
        <a href="/">React Shopping Cart</a>
      </header>
      <main>
        <div className="cart">
          <Filter
            product={products}
            Size={Size}
            Sort={Sort}
            setSize={setSize}
            setSort={setSort}
          />

          <CartItem
            product={products}
            setproduct={setproducts}
            Size={Size}
            Sort={Sort}
          />
        </div>
        <div className="shopping"> <ShoppingItem/></div>
      </main>
      <footer>All rigths reserved.</footer>
    </div>
  );
}

export default App;
